package com.cp.arithmetic;
public class Swap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=10,b=20;
		a=a+b;
		b=a-b;
		a=a-b;
		System.out.println("value of a is: "+a+" value of b is: "+b);

	}

}


